// src/components/Sidebar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Sidebar.css';

function Sidebar() {
  const [isDropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  return (
    <div className="sidebar">
      <h2>Menu</h2>
      <Link to="/tamil">Tamil Movies</Link>
      <Link to="/english">English Movies</Link>
      <Link to="/hindi">Hindi Movies</Link>
      <Link to="/cartoon">Cartoon Movies</Link>
      <div className="icon" onClick={toggleDropdown}>
        &#x22EE; {/* Three dots icon */}
      </div>
      <div className={`dropdown ${isDropdownOpen ? 'show' : ''}`}>
        <Link to="/action">Action</Link>
        <Link to="/drama">Drama</Link>
        <Link to="/comedy">Comedy</Link>
        <div className="close-btn" onClick={toggleDropdown}>Close</div>
      </div>
    </div>
  );
}

export default Sidebar;
