// src/components/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import Sidebar from './Sidebar';

function Home() {
  const homeContainerStyles = {
    display: 'flex',
    flexDirection: 'row',
  };

  const contentStyles = {
    marginLeft: '250px', // Sidebar width
    padding: '20px',
    backgroundImage: 'url(/images/background.jpg)',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh',
    width: '100%',
  };

  const sectionStyles = {
    backgroundColor: 'rgba(255, 255, 255, 0.9)', // Semi-transparent white
    borderRadius: '8px',
    padding: '20px',
    margin: '20px',
    textAlign: 'center',
    maxWidth: '800px',
  };

  const categoryGridStyles = {
    display: 'grid',
    gridTemplateColumns: 'repeat(3, 1fr)',
    gap: '20px',
  };

  const categoryCardStyles = {
    backgroundColor: '#f0f0f0', // Light gray
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
  };

  const linkStyles = {
    display: 'block',
    marginTop: '10px',
    color: '#333',
    textDecoration: 'none',
    fontWeight: 'bold',
  };

  return (
    <div style={homeContainerStyles}>
      <Sidebar />
      <div style={contentStyles}>
        <header>
          <h1>Welcome to Movie Page</h1>
        </header>
        <main>
          <section style={sectionStyles}>
            <h2>Welcome!</h2>
            <p>Explore a variety of movies from different categories.</p>
          </section>
          <section style={sectionStyles}>
            <h2>Categories</h2>
            <div style={categoryGridStyles}>
              <div style={categoryCardStyles}>
                <h3>Tamil Movies</h3>
                <p>Discover the latest Tamil releases.</p>
                <Link to="/tamil" style={linkStyles}>View Tamil Movies</Link>
              </div>
              <div style={categoryCardStyles}>
                <h3>English Movies</h3>
                <p>Explore popular English language movies.</p>
                <Link to="/english" style={linkStyles}>View English Movies</Link>
              </div>
              <div style={categoryCardStyles}>
                <h3>Hindi Movies</h3>
                <p>Enjoy Bollywood and Hindi cinema.</p>
                <Link to="/hindi" style={linkStyles}>View Hindi Movies</Link>
              </div>
              <div style={categoryCardStyles}>
                <h3>Cartoon Movies</h3>
                <p>Entertain your kids with animated films.</p>
                <Link to="/cartoon" style={linkStyles}>View Cartoon Movies</Link>
              </div>
            </div>
          </section>
        </main>
        <footer>
          <p>Â© 2024 Movie Page. All rights reserved.</p>
        </footer>
      </div>
    </div>
  );
}

export default Home;
