// src/components/Sidebar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Sidebar() {
  const [isDropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  const sidebarStyles = {
    height: '100%',
    width: '250px',
    position: 'fixed',
    top: 0,
    left: 0,
    backgroundColor: '#333',
    color: 'white',
    paddingTop: '20px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    boxShadow: '2px 0 5px rgba(0,0,0,0.5)',
  };

  const linkStyles = {
    padding: '10px 15px',
    textDecoration: 'none',
    color: 'white',
    display: 'block',
    width: '100%',
    textAlign: 'center',
  };

  const dropdownStyles = {
    backgroundColor: '#444',
    width: '100%',
    display: isDropdownOpen ? 'block' : 'none',
  };

  const iconStyles = {
    cursor: 'pointer',
    fontSize: '24px',
    padding: '10px 15px',
    width: '100%',
    textAlign: 'center',
  };

  return (
    <div style={sidebarStyles}>
      <h2>Menu</h2>
      <Link to="/tamil" style={linkStyles}>Tamil Movies</Link>
      <Link to="/english" style={linkStyles}>English Movies</Link>
      <Link to="/hindi" style={linkStyles}>Hindi Movies</Link>
      <Link to="/cartoon" style={linkStyles}>Cartoon Movies</Link>
      <div style={iconStyles} onClick={toggleDropdown}>
        &#x22EE; {/* Three dots icon */}
      </div>
      <div style={dropdownStyles}>
        <Link to="/action" style={linkStyles}>Action</Link>
        <Link to="/drama" style={linkStyles}>Drama</Link>
        <Link to="/comedy" style={linkStyles}>Comedy</Link>
        <div style={linkStyles} onClick={toggleDropdown}>Close</div>
      </div>
    </div>
  );
}

export default Sidebar;
